// Step 1: Safely access and process the input content
let html = '';
try {
  let rawOutput = '';
  if ($input.first().json && $input.first().json.output) {
    rawOutput = $input.first().json.output;
  } else if ($input.first().json && typeof $input.first().json === 'string') {
    rawOutput = $input.first().json;
  } else if ($input.first().json) {
    if ($input.first().json.message && $input.first().json.message.content) {
      rawOutput = $input.first().json.message.content;
    } else if ($input.first().json.data && typeof $input.first().json.data === 'string') {
      rawOutput = $input.first().json.data;
    }
  }

  html = rawOutput || '';
  html = html.replace(/^```html\n?/, '').replace(/```$/, '');
  html = html.replace(/\\n/g, '\n');
  html = html.trim();
} catch (error) {
  console.warn('Error accessing or processing input content. Error:', error.message);
  return [{
    json: {
      recipe_name: null,
      recipe_ingredients: [],
      recipe_directions: [],
      amazon_recipe_links: [],
      prep_time: null,
      brief_description: null,
      recipe_image_url: null,
    },
  }];
}

// Generate recipe image URL based on recipe data - SINGLE UNIFIED FUNCTION
const generateRecipeImageUrl = (recipeName, ingredients) => {
  // Curated image mapping for better results
  const imageMap = {
