  };

  // Create search text from recipe name and ingredients
  const searchText = (recipeName + ' ' + ingredients.join(' ')).toLowerCase();

  // Find the best matching image
  let bestMatch = null;
  let longestMatch = 0;

  for (const [keyword, imageUrl] of Object.entries(imageMap)) {
    if (searchText.includes(keyword) && keyword.length > longestMatch) {
      bestMatch = imageUrl;
      longestMatch = keyword.length;
    }
  }

  // Return best match or fallback
  return bestMatch || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?q=80&w=1200&auto=format&fit=crop';
};

if (!html) {
  console.warn('Content was empty or inaccessible after initial processing.');
  return [{
    json: {
      recipe_name: null,
      recipe_ingredients: [],
      recipe_directions: [],
      amazon_recipe_links: [],
      prep_time: null,
      brief_description: null,
      recipe_image_url: null,
    },
  }];
}

// Initialize output structure
let recipeData = {
  recipe_name: null,
  recipe_ingredients: [],
  recipe_directions: [],
  amazon_recipe_links: [],
  prep_time: null,
  brief_description: null,
  recipe_image_url: null,
};

// Check if the content is JSON format
let isJsonFormat = false;
let jsonData = null;

try {
  // Try to parse as JSON first
  jsonData = JSON.parse(html);
  isJsonFormat = true;
  console.log('Detected JSON format input');
} catch (e) {
  // Not JSON, proceed with markdown parsing
  console.log('Detected markdown format input');
}

if (isJsonFormat && jsonData) {
  // Handle JSON format
  recipeData.recipe_name = jsonData.recipe_name || null;
  recipeData.brief_description = jsonData.cultural_context || null;
  recipeData.prep_time = jsonData.estimated_preparation_time || null;
  recipeData.recipe_image_url = jsonData.recipe_image_url || null;

  // Extract ingredients from nested structure
  if (jsonData.ingredients) {
    const allIngredients = [];
    Object.values(jsonData.ingredients).forEach(section => {
      if (Array.isArray(section)) {
        section.forEach(ingredient => {
          const cleanIngredient = ingredient.replace(/^-\s+/, '').trim();
          if (cleanIngredient) {
            allIngredients.push(cleanIngredient);
          }
        });
      }
    });
    recipeData.recipe_ingredients = allIngredients;
  }

  // Extract directions
  if (jsonData.preparation_instructions && Array.isArray(jsonData.preparation_instructions)) {
    recipeData.recipe_directions = jsonData.preparation_instructions.map(step => 
      step.replace(/^\d+\.\s*/, '').trim()
    );
  }

  // Extract Amazon links
  if (jsonData.amazon_ingredient_links) {
    recipeData.amazon_recipe_links = Object.values(jsonData.amazon_ingredient_links);
  }

} else {
  // Handle markdown format (existing parsing logic)
  const lines = html.split('\n');
  let currentSection = null;
  let tempIngredients = [];
  let tempDirections = [];
  let tempLinks = [];
  let tempDescription = [];

  // Helper functions
  const isHeadingMatch = (line, keywords) => {
    const lower = line.toLowerCase().trim();
    return keywords.some(keyword => 
      lower.includes(keyword.toLowerCase()) && 
      (lower.startsWith('#') || lower.endsWith(':'))
    );
  };

  const extractUrl = (text) => {
    const match = text.match(/(https?:\/\/[^\s]+)/);
    return match ? match[1] : null;
  };

  // Parse line by line
  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];
    const trimmed = line.trim();

    if (!trimmed) {
      // Skip empty lines
    } else if (!recipeData.recipe_name && trimmed.match(/^#+\s+/)) {
      // Extract recipe name from the FIRST heading (# or ##) that appears
      const potentialName = trimmed.replace(/^#+\s*/, '').trim();

      // Skip common section headers that aren't recipe names
      const skipKeywords = [
        'ingredients', 'preparation', 'instructions', 'amazon', 'links', 
        'estimated', 'prep time', 'preparation time', 'garnish'
      ];

      const isSkippable = skipKeywords.some(keyword => 
        potentialName.toLowerCase().includes(keyword)
      );

      if (!isSkippable && potentialName.length > 0) {
        recipeData.recipe_name = potentialName;
      }
    } else if (isHeadingMatch(trimmed, ['Ingredients'])) {
      currentSection = 'ingredients';
    } else if (isHeadingMatch(trimmed, ['Preparation Instructions', 'Instructions', 'Recipe Instructions'])) {
      currentSection = 'instructions';
    } else if (isHeadingMatch(trimmed, ['Amazon Ingredient Links', 'Amazon Links'])) {
      currentSection = 'amazon_links';
    } else if (isHeadingMatch(trimmed, ['Estimated Preparation Time', 'Prep Time', 'Preparation Time'])) {
      currentSection = 'prep_time';
    } else if (isHeadingMatch(trimmed, ['Recipe Image', 'Image URL'])) {
      currentSection = 'image_url';
    } else {
      // Content parsing based on current section
      if (currentSection === 'ingredients') {
        if (!(trimmed.startsWith('#') || trimmed.endsWith(':'))) {
          if (trimmed.startsWith('- ')) {
            const ingredient = trimmed.replace(/^-\s+/, '').trim();
            const cleanIngredient = ingredient.replace(/\s+\([^)]*\)$/, '').trim();
            if (cleanIngredient) {
              tempIngredients.push(cleanIngredient);
            }
          }
        }
      } else if (currentSection === 'instructions') {
        if (!(trimmed.startsWith('#') || trimmed.startsWith('**') || trimmed === '---')) {
          if (/^\d+\./.test(trimmed)) {
            const step = trimmed.replace(/^\d+\.\s*/, '').trim();
            if (step) {
              tempDirections.push(step);
            }
          }
        }
      } else if (currentSection === 'prep_time') {
        if (trimmed.toLowerCase().includes('total') || trimmed.toLowerCase().includes('approximately')) {
          const timeMatch = trimmed.match(/(\d+)\s*minutes?/i);
          if (timeMatch) {
            recipeData.prep_time = timeMatch[1] + ' minutes';
          }
        }
      } else if (currentSection === 'amazon_links') {
        if (trimmed.startsWith('- ')) {
          const url = extractUrl(trimmed);
          if (url) {
            tempLinks.push(url);
          }
        }
      } else if (currentSection === 'image_url') {
        if (trimmed.startsWith('- ') || trimmed.startsWith('http')) {
          const url = extractUrl(trimmed) || trimmed.replace(/^-\s+/, '').trim();
          if (url) {
            recipeData.recipe_image_url = url;
          }
        }
      } else if (!currentSection && !trimmed.startsWith('#') && !trimmed.startsWith('---')) {
        tempDescription.push(trimmed);
      }
    }
  }

  // Assign parsed data for markdown format
  recipeData.recipe_ingredients = tempIngredients;
  recipeData.recipe_directions = tempDirections;
  recipeData.amazon_recipe_links = tempLinks;

  if (tempDescription.length > 0) {
    recipeData.brief_description = tempDescription.join(' ').trim();
  }
}

// Fix image URL if it's not a proper image URL or generate one
if (!recipeData.recipe_image_url || 
    recipeData.recipe_image_url.includes('/s/photos/') || 
    !recipeData.recipe_image_url.startsWith('http')) {
  recipeData.recipe_image_url = generateRecipeImageUrl(
    recipeData.recipe_name, 
    recipeData.recipe_ingredients
  );
}

// Fallback recipe name generation if none was found
if (!recipeData.recipe_name && recipeData.recipe_ingredients.length > 0) {
  const firstFewIngredients = recipeData.recipe_ingredients.slice(0, 2).map(ing => 
    ing.split(',')[0].split(' ').slice(-1)[0]
  ).join(' & ');
  recipeData.recipe_name = `Creative Recipe: ${firstFewIngredients} Fusion`;
}

console.log('Parsed recipe data:', recipeData);

return [{ json: recipeData }];
